def rgb(color, default=(0,0,0)):
    """ return rgb tuple for named color in rgb.txt or a hex color """
    c = color.lower()
    if c[0:1] == '#' and len(c)==7:
        r,g,b = c[1:3], c[3:5], c[5:]
        r,g,b = [int(n, 16) for n in (r, g, b)]
        return (r,g,b)

    c = c.replace(' ', '').replace('gray', 'grey')
    if c in X11_COLORS:
        return X11_COLORS[c]
    return default

def hex2rgb(hexval):
    c = hexval
    if c[0:1] == '#' and len(c)==7:
        r,g,b = c[1:3], c[3:5], c[5:]
        r,g,b = [int(n, 16) for n in (r, g, b)]
        return (r,g,b)
    return rgb


def rgb2hex(rgb):
    if isinstance(rgb, tuple):
        col = '#%02x%02x%02x' % rgb[:3]
    else:
        try:
            col = '#%02x%02x%02x' % (rgb.Red(), rgb.Green(), rgb.Blue())
        except:
            col = "#000000"
    return col.lower()


def hexcolor(color):
    " returns hex color given a tuple, wx.Color, or X11 color"
    # first, if this is a hex color already, return!
    # Python 3: needs rewrite for str/unicode change
    if isinstance(color, str):
        if color[0] == '#' and len(color) in (7, 9):
            return color.lower()

    # now, get color to an rgba tuple
    cvals = (0, 0, 0)
    if isinstance(color, tuple):
        cvals = color
    elif isinstance(color, list):
        cvals = tuple(color)
    elif isinstance(color, str):
        c = color.lower().replace(' ', '').replace('gray', 'grey')
        if c in X11_COLORS:
            cvals = X11_COLORS[c]

    else:
        try:
            cvals = color.Red(), color.Green(), color.Blue()
        except:
            pass

    # convert cvals to hex color
    col = '#'  +  ''.join(['%02x' % c for c in cvals])
    return col.lower()

